function loadFile4Path(t) {
  var timeRandom = Math.floor(Date.now() / 1000);
  var p = "/" + t.Key + "/" + t.Name + ".html";
  var ND = t.Name.charAt(0).toUpperCase() + t.Name.slice(1);
  document.write(
    '<script src="' +
      p.replace(".html", "Controller.js?v=" + timeRandom) +
      '"></script>'
  );
  document.write(
    '<script>app.directive("bakco' +
      ND +
      '", function () {return {restrict: "CAE",controller: "' +
      ND.toLowerCase() +
      'Controller",templateUrl: "' +
      p +
      '"}});</script>'
  );
}

function testInterceptor() {
  return {
    request: function (config) {
      config.headers["Authorization"] = "Bearer 123";
      return config;
    },
    requestError: function (config) {
      return config;
    },
    response: function (res) {
      return res;
    },
    responseError: function (res) {
      return res;
    },
  };
}
var app = angular.module("rootApp", ["ngRoute", "ngSanitize"]);

app.config([
  "$locationProvider",
  function ($locationProvider) {
    $locationProvider.hashPrefix("");
    $locationProvider.html5Mode({
      enabled: false,
      requireBase: false,
      rewriteLinks: false,
    });
  },
]);
app
  .factory("testInterceptor", testInterceptor)
  .config(function ($httpProvider) {
    $httpProvider.interceptors.push("testInterceptor");
  });
for (x in sysPath) {
  loadFile4Path(sysPath[x]);
}
